// Import React and necessary hooks
import React, { useState } from 'react';
// Import the DatePicker component and its CSS
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
// Import custom CSS for the HotelSelector
import './HotelSelector.css';
// Import useNavigate hook from react-router-dom
import { useNavigate } from 'react-router-dom';

const HotelSelector = () => {
    const navigate = useNavigate();
    const [searchParams, setSearchParams] = useState({
        hotelChain: 'default',
        rating: 'default',
        maxCost: '',
        location: '',
        startDate: new Date(),
        endDate: new Date(),
        category: 'default',
        numberOfRooms: 1,
        roomType: 'default',
    });

    // Function to handle search
    const handleSearch = async () => {
        const queryString = new URLSearchParams(searchParams).toString();
        try {
            const response = await fetch(`/api/search?${queryString}`);
            const data = await response.json();
            navigate('/hotel-list', { state: { searchResults: data } });
        } catch (error) {
            console.error('Search failed:', error);
        }
    };

    // Function to handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setSearchParams(prevParams => ({
            ...prevParams,
            [name]: value,
        }));
    };

    // Function to handle date changes
    const handleDateChange = (dates) => {
        const [start, end] = dates;
        setSearchParams(prevParams => ({
            ...prevParams,
            startDate: start,
            endDate: end,
        }));
    };

    // Navigation functions
    const navigateToCreateAccount = () => navigate('/create-account');
    const navigateToLogin = () => navigate('/login');

    // JSX for the component
    return (
        <div className="hotel-search-area">
            <h2 className="search-title">Find Your Ideal Hotel</h2>
            <form className="search-form">
                {/* Input fields and selections */}
                <div className="form-group">
                    <select name="hotelChain" onChange={handleChange} value={searchParams.hotelChain} className="form-control">
                        <option value="default">Any Hotel Chain</option>
                        {/* Hotel chain options */}
                    </select>
                </div>
                {/* Additional form groups for other inputs */}
                <div className="date-picker-group">
                    <DatePicker
                        selectsRange={true}
                        startDate={searchParams.startDate}
                        endDate={searchParams.endDate}
                        onChange={handleDateChange}
                        className="form-control date-picker"
                    />
                </div>
                <button type="button" className="search-button" onClick={handleSearch}>Search</button>
                <div className="auth-buttons">
                    <button type="button" className="auth-button create-account-button" onClick={navigateToCreateAccount}>Create Account</button>
                    <button type="button" className="auth-button login-button" onClick={navigateToLogin}>Login</button>
                </div>
            </form>
        </div>
    );
};

export default HotelSelector;
