import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateAccount.css';

const CreateAccount = () => {
    const [accountCreated, setAccountCreated] = useState(false);
    const [showSIN, setShowSIN] = useState(false); // State to toggle SIN visibility
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = {
            name: event.target[0].value, // Assuming the order of inputs. Better to use form refs or IDs
            email: event.target[2].value,
            password: event.target[3].value,
            phone: "", // Add an input for phone or manage it differently
            streetNumber: "", // Add inputs for these fields or manage them differently
            streetName: "",
            region: "",
            city: "",
            postalCode: ""
        };

        try {
            const response = await fetch('http://localhost:6060/customer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // To ensure cookies are sent and received for CORS requests
                body: JSON.stringify(formData),
            });

            const responseData = await response.json();
            if(response.ok) {
                setAccountCreated(true); // Assuming you have a state to manage this
                // Redirect or show a success message
            } else {
                // Handle errors (e.g., show an error message)
                console.error('Account creation failed:', responseData);
            }
        } catch (error) {
            console.error('Failed to create account:', error);
        }
    };


    // Toggle SIN visibility
    const toggleSINVisibility = () => {
        setShowSIN(!showSIN);
    };

    return (
        <div className="create-account-container">
            {accountCreated ? (
                <div className="account-created-message">
                    <h2>Congratulations, your account has been created!</h2>
                    <button onClick={() => navigate('/')}>Go Back to Hotels</button>
                </div>
            ) : (
                <form className="create-account-form" onSubmit={handleSubmit}>
                    <input type="text" placeholder="First Name" required />
                    <input type="text" placeholder="Last Name" required />
                    <input type="email" placeholder="Email Address" required />
                    <input type="password" placeholder="Password" required />
                    <input type="text" placeholder="Address" required />
                    <div className="sin-input-container">
                        <input
                            type={showSIN ? "text" : "password"}
                            placeholder="SIN (e.g., 123-456-789)"
                            pattern="\d{3}-\d{3}-\d{3}"
                            title="SIN format: 123-456-789"
                            required
                        />
                        <button type="button" onClick={toggleSINVisibility} className="toggle-sin">
                            {showSIN ? "Hide" : "Show"}
                        </button>
                    </div>
                    <button type="submit" className="create-account-submit">Create Account</button>
                    {/* Adding a 'Back to Hotels' button inside the form for consistency */}
                    <button type="button" onClick={() => navigate('/')} className="back-button">
                        Back to Hotels
                    </button>
                </form>
            )}
        </div>
    );
};

export default CreateAccount;
