import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './HotelSelector.css';
import { useNavigate } from 'react-router-dom';

const HotelSelector = () => {
    const navigate = useNavigate();
    const [searchParams, setSearchParams] = useState({
        hotelChain: 'default',
        rating: 'default',
        maxCost: '',
        location: '',
        startDate: new Date(),
        endDate: new Date(),
        category: 'default',
        numberOfRooms: 1,
        roomType: 'default',
    });

    const handleSearch = async () => {
        const queryString = new URLSearchParams(searchParams).toString();
        try {
            const response = await fetch(`/api/search?${queryString}`);
            const data = await response.json();
            navigate('/hotel-list', { state: { searchResults: data } });
        } catch (error) {
            console.error('Search failed:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSearchParams(prevParams => ({
            ...prevParams,
            [name]: value,
        }));
    };

    const handleDateChange = (dates) => {
        const [start, end] = dates;
        setSearchParams(prevParams => ({
            ...prevParams,
            startDate: start,
            endDate: end,
        }));
    };

    const navigateToCreateAccount = () => navigate('/create-account');
    const navigateToLogin = () => navigate('/login');

    return (
        <div className="hotel-search-area">
            <h2 className="search-title">Find Your Ideal Hotel</h2>
            <div className="form-group">
                <select name="hotelChain" onChange={handleChange} value={searchParams.hotelChain} className="form-control">
                    <option value="default">Any Hotel Chain</option>
                    {/* More hotel chains can be dynamically added here */}
                </select>
                <select name="rating" onChange={handleChange} value={searchParams.rating} className="form-control">
                    <option value="default">Choose Rating (1-5)</option>
                    {/* Rating options can be dynamically generated here */}
                </select>
                <select name="category" onChange={handleChange} value={searchParams.category} className="form-control">
                    <option value="default">Select a Category</option>
                    <option value="Luxurious">Luxurious</option>
                    <option value="Standard">Standard</option>
                    <option value="Economic">Economic</option>
                </select>
                <input type="number" name="numberOfRooms" placeholder="Number of Rooms" min="1" onChange={handleChange} value={searchParams.numberOfRooms} className="form-control" />
                <select name="roomType" onChange={handleChange} value={searchParams.roomType} className="form-control">
                    <option value="default">Select Room Type</option>
                    <option value="Single">Single</option>
                    <option value="Double">Double</option>
                    {/* Additional room types can be added here */}
                </select>
                <input type="number" name="maxCost" placeholder="Maximum Cost per Night" min="0" onChange={handleChange} value={searchParams.maxCost} className="form-control" />
                <input type="text" name="location" placeholder="Location (City)" onChange={handleChange} value={searchParams.location} className="form-control" />
                <div className="date-picker-group">
                    <DatePicker selectsRange={true} startDate={searchParams.startDate} endDate={searchParams.endDate} onChange={handleDateChange} className="form-control date-picker" />
                </div>
            </div>
            <button className="search-button" onClick={handleSearch}>Search</button>
            <div className="auth-buttons">
                <button className="auth-button" onClick={navigateToCreateAccount}>Create Account</button>
                <button className="auth-button login-button" onClick={navigateToLogin}>Login</button>
            </div>
        </div>
    );
};

export default HotelSelector;
